const results = $input.first().json.results || [];
const query = $('Search Places Webhook').first().json.body.query;
const location = $('Search Places Webhook').first().json.body.location;

const places = results.slice(0, 10).map((place, index) => ({
  id: place.place_id,
  name: place.name,
  address: place.formatted_address,
  rating: place.rating,
  userRatingsTotal: place.user_ratings_total,
  priceLevel: place.price_level,
  types: place.types,
  location: place.geometry?.location,
  photos: place.photos?.slice(0, 3).map(p => ({
    reference: p.photo_reference,
    width: p.width,
    height: p.height
  })),
  openNow: place.opening_hours?.open_now,
  rank: index + 1
}));

return {
  json: {
    success: true,
    query: query,
    location: location,
    count: places.length,
    places: places
  }
};

const originalData = $('Process Places').first().json;
const aiResponse = $input.first().json;

let enhancedPlaces;
try {
  const content = aiResponse.message?.content || aiResponse.choices?.[0]?.message?.content;
  enhancedPlaces = JSON.parse(content);
} catch (e) {
  // If AI parsing fails, use original data with default enhancements
  enhancedPlaces = originalData.places.map(place => ({
    ...place,
    description: `A popular attraction in ${originalData.query}`,
    bestTimeToVisit: 'morning',
    estimatedDuration: 90,
    tip: 'Arrive early to avoid crowds'
  }));
}

return {
  json: {
    success: true,
    query: originalData.query,
    count: enhancedPlaces.length,
    places: enhancedPlaces
  }
};
